
public class bookTest5_4_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=1;
		while(num1<=100){
			System.out.print(num1+"\t");
			num1++;
		}
		System.out.println();
		do{
			System.out.print(--num1+"\t");
		}while(num1>1);
				
	}

}
