
public class 실습 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int num1=0;num1<=1000;num1++){
			
			if(num1%2==0)
				System.out.println(num1+"은(는) 2의 배수입니다.");
			else if(num1%3==0)
				System.out.println(num1+"은(는) 3의 배수입니다.");
		}
	}

}
